from Crypto.Util.number import *
from random import *
from secret import flag

m = bytes_to_long(flag)

p = getPrime(512)
q = getPrime(512)
n = p*q
e = getPrime(64)
y = randint(1,n-1)

enc1 = (2*m-y) % n
enc2 = (pow(m,77797,n) + pow(y,99979,n)) % n
enc3 = pow(m,e,n)

print("n =",n)
print("e =",e)
print("enc1 =",enc1)
print("enc2 =",enc2)
print("enc3 =",enc3)

